import placeHolder from "../../Assets/Images/placeholder.png";
// import videoDetails from "../../Data/video-details.json";
import "./commentCard.scss";

function CommentCard({ videoDetails, selectedIndex }) {
  // console.log(videoDetails.filter((el, i) => i !== selectedIndex)).map((el) =>
  console.log(videoDetails.filter((el, i) => i === selectedIndex));
  return (
    <>
      <section className="comments-archieve">
        {videoDetails
          .filter((el, i) => i === selectedIndex)
          .map((el) => {
            if (el.comments) {
              return (
                <div key={el.id} className="comments-archieve__outer">
                  <hr className="comments-archieve__hr" />
                  <div className="comments-archieve__comment">
                    <img
                      className="comments-archieve__icon"
                      src={placeHolder}
                    />
                    <div className="comments-archieve__com-container">
                      <div className="comments-archieve__name-date">
                        <span>{name}</span>
                        <span className="comments-archieve__span-right">
                          {new Date(timestamp).toLocaleDateString("en-US", {
                            day: "2-digit",
                            month: "2-digit",
                            year: "numeric",
                          })}
                        </span>
                      </div>
                      <div className="comments-archieve__text">{comment}</div>
                    </div>
                  </div>
                </div>
              );
            }
          })}
      </section>
    </>
  );
}

export default CommentCard;
